import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getPostById, likePost, unlikePost, addComment } from "../services/api";

export default function SingleBlog() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [commentText, setCommentText] = useState("");

  const dummyUserId = "user123";

  // Fetch post
  useEffect(() => {
  getPostById(id)
    .then((res) => setPost(res.data.post)) // fix here
    .catch((err) => console.error(err));
}, [id]);


  const handleLike = async () => {
    if (!post?._id) return;
    try { 
      const res = await likePost(post._id, dummyUserId);
      setPost(res.data.post);
    } catch (err) {
      console.error(err);
    }
  };

 
  const handleUnlike = async () => {
  if (!post?._id) return;

  // find the like belonging to this user
  const userLike = post.likes?.find(like => like.userId === dummyUserId);
  if (!userLike) return; // user hasn't liked, nothing to unlike

  try {
    const res = await unlikePost(post._id, userLike._id);
    setPost(res.data.post);
  } catch (err) {
    console.error(err);
  }
};

  const handleAddComment = async () => {
    if (!post?._id || !commentText.trim()) return;
    try {
      const res = await addComment(post._id, dummyUserId, commentText);
      setPost(res.data.post);
      setCommentText("");
    } catch (err) {
      console.error(err);
    }
  };

  if (!post) return <p>Loading...</p>;

  return (
    <div className="page">
      <h2>{post.title}</h2>
      <p>{post.body}</p>

      {/* Likes */}
      <div>
        <p>Likes: {post.likes?.length || 0}</p>
        <button onClick={handleLike}>Like</button>
        <button onClick={handleUnlike}>Unlike</button>
      </div>

      {/* Comments */}
      <div>
        <h3>Comments</h3>
        <ul>
          {post.comments?.map((c) => (
            <li key={c._id}>{c.body}</li>
          ))}
        </ul>
        <input
          type="text"
          placeholder="Write a comment..."
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        />
        <button onClick={handleAddComment}>Add Comment</button>
      </div>
    </div>
  );
}
