import { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import { getPostById, likePost, unlikePost, addComment } from "../services/api";

export default function SingleBlog() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [commentText, setCommentText] = useState("");

  const dummyUserId = "user123"; 
  const ws = useRef(null); // WebSocket reference

  // Fetch post on mount
  useEffect(() => {
    getPostById(id)
      .then((res) => setPost(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  // Setup WebSocket connection
  useEffect(() => {
    ws.current = new WebSocket("ws://localhost:4001");

    ws.current.onopen = () => console.log("WebSocket connected");

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.postId === post?._id) {
        setPost(data.post); // update post in real-time
      }
    };

    ws.current.onclose = () => console.log("WebSocket disconnected");

    return () => ws.current.close();
  }, [post?._id]);

  const sendWsUpdate = (updatedPost) => {
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({ postId: updatedPost._id, post: updatedPost }));
    }
  };

  // Like post
  const handleLike = async () => {
    try {
      const res = await likePost(post._id, dummyUserId);
      setPost(res.data.post);
      sendWsUpdate(res.data.post); // send update to other clients
    } catch (err) {
      console.error(err);
    }
  };

  // Unlike post
  const handleUnlike = async () => {
    try {
      if (post.likes.length === 0) return;
      const likeId = post.likes[0]._id;
      const res = await unlikePost(post._id, likeId);
      setPost(res.data.post);
      sendWsUpdate(res.data.post);
    } catch (err) {
      console.error(err);
    }
  };

  // Add comment
 const handleAddComment = async () => {
  if (!post || !post._id || !commentText.trim()) return;
  try {
    const res = await addComment(post._id, dummyUserId, commentText);
    setPost(res.data.post);
    setCommentText("");
  } catch (err) {
    console.error(err);
  }
};

  if (!post) return <p>Loading...</p>;

  return (
    <div className="page">
      <h2>{post.title}</h2>
      <p>{post.body}</p>

      {/* Likes */}
      <div>
    
    <p>Likes: {post.likes?.length || 0}</p>
        <button onClick={handleLike}>Like</button>
        <button onClick={handleUnlike}>Unlike</button>
      </div>

      {/* Comments */}
      <div>
        <h3>Comments</h3>
        <ul>
  {post.comments?.map((c) => (
    <li key={c._id}>{c.body}</li>
  ))}
</ul>
        <input
          type="text"
          placeholder="Write a comment..."
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        />
        <button onClick={handleAddComment}>Add Comment</button>
      </div>
    </div>
  );
}
