import axios from "axios";

const API_URL = "http://localhost:3000"; // your backend port

// Fetch all posts
export const fetchPosts = () => axios.get(`${API_URL}/posts`);

// Create a new post
export const createPost = (postData) => axios.post(`${API_URL}/posts/create`, postData);

// Delete a post
export const deletePost = (id) => axios.delete(`${API_URL}/posts/${id}`);

// For your React code
export const getPosts = () => fetchPosts();
export const getPostById = (id) => axios.get(`${API_URL}/posts/${id}`);
