import { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import { getPostById, likePost, unlikePost, addComment } from "../services/api";

export default function SingleBlog() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [commentText, setCommentText] = useState("");
  const ws = useRef(null);

  const dummyUserId = "user123"; // replace with real user later

  // 🔹 Fetch post
  useEffect(() => {
    getPostById(id)
      .then((res) => setPost(res.data))
      .catch((err) => console.error(err));
  }, [id]);

  // 🔹 Setup WebSocket
  useEffect(() => {
  ws.current = new WebSocket("ws://localhost:4000"); // ✅ same port as backend

    ws.current.onopen = () => console.log("✅ WebSocket connected");

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.postId === id) {
        setPost(data.post);
      }
    };

    ws.current.onclose = () => console.log("❌ WebSocket disconnected");

    return () => {
      if (ws.current) ws.current.close();
    };
  }, [id]);

  // 🔹 Send update to WS
  const sendWsUpdate = (updatedPost) => {
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({ postId: updatedPost._id, post: updatedPost }));
    }
  };

  // 🔹 Like post
  const handleLike = async () => {
    if (!post?._id) return;
    try {
      const res = await likePost(post._id, dummyUserId);
      setPost(res.data.post);
      sendWsUpdate(res.data.post);
    } catch (err) {
      console.error(err);
    }
  };

  // 🔹 Unlike post
  const handleUnlike = async () => {
    if (!post?._id || post.likes?.length === 0) return;
    try {
      const likeId = post.likes[0]._id;
      const res = await unlikePost(post._id, likeId);
      setPost(res.data.post);
      sendWsUpdate(res.data.post);
    } catch (err) {
      console.error(err);
    }
  };

  // 🔹 Add comment
  const handleAddComment = async () => {
    if (!post?._id || !commentText.trim()) return;
    try {
      const res = await addComment(post._id, dummyUserId, commentText);
      setPost(res.data.post);
      setCommentText("");
      sendWsUpdate(res.data.post);
    } catch (err) {
      console.error(err);
    }
  };

  if (!post) return <p>Loading...</p>;

  return (
    <div className="page">
      <h2>{post.title}</h2>
      <p>{post.body}</p>

      {/* Likes */}
      <div>
        <p>Likes: {post.likes?.length || 0}</p>
        <button onClick={handleLike}>👍 Like</button>
        <button onClick={handleUnlike}>👎 Unlike</button>
      </div>

      {/* Comments */}
      <div>
        <h3>Comments</h3>
        <ul>
          {post.comments?.map((c) => (
            <li key={c._id}>{c.body}</li>
          ))}
        </ul>
        <input
          type="text"
          placeholder="Write a comment..."
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        />
        <button onClick={handleAddComment}>💬 Add Comment</button>
      </div>
    </div>
  );
}
